#include <iostream>

using namespace std;

class Solution {
public:
    bool isMatch(const char *s, const char *p) {
        int n = (int)strlen(s), m = (int)strlen(p);
		if (n == 0) return strcmp(p, "*") == 0 || strcmp(p, "") == 0;
		bool dp[2][n + 1];
		int notstar[m + 1], sustar[m + 1];
		notstar[0] = 0;sustar[m] = 0;
		for (int i = 1; i <= m; ++i) notstar[i] = notstar[i - 1] + (p[i - 1] != '*' ? 1 : 0);
		for (int i = m - 1; i >= 0; --i) sustar[i] = sustar[i + 1] + (p[i - 1] != '*' ? 1 : 0);
		
		memset(dp, false, sizeof(dp));
		dp[0][0] = true;
		
		for (int j = 1; j <= m; ++j) {
			int now = j % 2, pre = 1 - now;
			memset(dp[now], false, sizeof(dp[now]));
			for (int i = max(0, notstar[j] - 1); i <= min(n, n - sustar[j] + 1); ++i) {
				if (i > 0) {
					if (p[j - 1] == s[i - 1]) dp[now][i] |= dp[pre][i - 1];
					if (p[j - 1] == '?') dp[now][i] |= dp[pre][i - 1];
					if (p[j - 1] == '*') dp[now][i] |= dp[pre][i] | dp[now][i - 1] | dp[pre][i - 1];
				}
				if (i == 0 && p[j - 1] == '*') dp[now][i] |= dp[pre][i];
			}
		}
		return dp[m % 2][n];
    }
};

int main(int argc, const char * argv[])
{
	cout << (new Solution())->isMatch("a", "*") << endl;
	cout << (new Solution())->isMatch("aa", "aa") << endl;
	cout << (new Solution())->isMatch("aaa", "aa") << endl;
	cout << (new Solution())->isMatch("aa", "*") << endl;
	cout << (new Solution())->isMatch("aa", "a*") << endl;
	cout << (new Solution())->isMatch("ab", "?*") << endl;
	cout << (new Solution())->isMatch("aab", "c*a*b") << endl;
	cout << (new Solution())->isMatch("abcede", "abc*d") << endl;
	cout << (new Solution())->isMatch("", "**") << endl;
	cout << (new Solution())->isMatch("", "*") << endl;
	cout << (new Solution())->isMatch("c", "*?*") << endl;
	
	// insert code here...
	std::cout << "Hello, World!\n";
    return 0;
}

